/**
 * Prints a progress message. If no message is passed, the message will be cleared.
 * @param text - The message to print.
 */
export declare function printProgress(text?: string): void;
/**
 * Prints an info message.
 * @param info - The info to print.
 */
export declare function printInfo(info: string): void;
/**
 * Prints a warning message.
 * @param warn - The warning to print.
 */
export declare function printWarning(warn: string): void;
/**
 * Prints an error message.
 * @param error - The error to print.
 */
export declare function printError(error: string): void;
